export default {
	getSchemas: () => {
		return(get_schemas.data.map(s => ({value: s.schema_name, label: s.schema_name})))
	},

	barChart: async () => {
		await tablesInSchemas.run();
		const dataSource = {
			chart: {
				xaxisname: "Schema",
				yaxisname: "Number of Tables",
				theme: "fusion",
				palettecolors: "#5D62B5",
			},
			data: tablesInSchemas.data		};
		return ({
			type: "bar2d",
			dataFormat: 'json',
			dataSource
		})
	},



	getDonutChart: () => {
		const dataSource = {
			chart: {
				caption: "Variation of datatypes in the selected table",
				showpercentvalues: "1",
				defaultcenterlabel: "Hover",
				decimals: "1",
				centerlabel: "$label: $value",
				theme: "fusion",
				showLegend: "0",
				showLabels: "0",
				showValues:"0"
			},
			data: dtypesinTable.data		};
		return ({
			type: "doughnut2d",
			dataFormat: 'json',
			dataSource
		})
	},

	onPageLoad: async() => {
		get_tables.run();
		dtypesinTable.run();
		get_columns.run();
	}
}
